mapper.select:
SELECT
    id, title, brief, user_id, cover_url,
    club_id, status, is_essence, created_at,
    view_count, like_count, comment_count,
    tag1, tag2, tag3
FROM post

    status = 'PASS'
    AND id < #{cursor}

        AND club_id IN

            #{clubId}



        AND user_id IN

            #{userId}



        AND (tag1 = #{tag} OR tag2 = #{tag} OR tag3 = #{tag})
    AND is_essence = #{isEssence}

        AND (
            title LIKE CONCAT('%', #{keyword}, '%')
            OR brief LIKE CONCAT('%', #{keyword}, '%')
        )

ORDER BY id DESC 
LIMIT #{pageSize}

mapper.insert:
INSERT INTO post (
    title, content, user_id, club_id, status, brief, tag1, tag2, tag3, cover_url
