    #{postCommitDTO.title}, #{postCommitDTO.content}, #{postCommitDTO.userId},
          #{postCommitDTO.clubId}, #{status}, #{brief},
          #{postCommitDTO.tag1}, #{postCommitDTO.tag2}, #{postCommitDTO.tag3},
          #{coverUrl}
)

mapper.update:
UPDATE post
title = #{postEditDTO.title}, content = #{postEditDTO.content}, tag1 = #{postEditDTO.tag1}, tag2 = #{postEditDTO.tag2}, tag3 = #{postEditDTO.tag3}, status = #{status}, updated_at = NOW(), brief = #{brief}, cover_url = #{coverUrl}
WHERE id = #{postEditDTO.id}

mapper.select:
