mapper.select:
SELECT
id, title, brief, user_id,
club_id, status, is_essence, created_at,
view_count, like_count, comment_count
FROM post

    status = 'PASS'
    AND club_id = #{clubId}
    AND id < #{cursor}

ORDER BY id DESC 
LIMIT #{pageSize}

mapper.insert:
INSERT INTO post (
    title, content, user_id, club_id, status, brief
) VALUES (
    #{postCommitDTO.title}, #{postCommitDTO.content}, #{postCommitDTO.userId}, #{postCommitDTO.clubId}, #{status}, #{brief}
)

mapper.update:
UPDATE post
title = #{title}, content = #{content}, status = #{status}, is_essence = #{isEssence}, updated_at = NOW(), brief = #{brief}
WHERE id = #{id}
