mapper.select:
SELECT * FROM post

    AND club_id = #{clubId}
    AND user_id = #{userId}
    AND status = #{status}
    AND is_essence = #{isEssence}

ORDER BY created_at DESC

mapper.insert:
INSERT INTO post (
    title, content, user_id, club_id, status
) VALUES (
    #{postCommitDTO.title}, #{postCommitDTO.content}, #{postCommitDTO.userId}, #{postCommitDTO.clubId}, #{status}
)

mapper.update:
UPDATE post
title = #{title}, content = #{content}, status = #{status}, is_essence = #{isEssence}, updated_at = NOW()
WHERE id = #{id}
