mapper.select:


SELECT id, user_id, report_type, report_target_type, report_target,
       reason, status, created_at, handled_at
FROM report

    AND report_target_type = #{reportTargetType}
    AND status = #{status}

LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM report

   AND report_target_type = #{reportTargetType}
   AND status = #{status}

mapper.insert:
INSERT INTO report (
    user_id, report_type, report_target_type, report_target, reason
) VALUES (
    #{userId}, #{reportType}, #{reportTargetType}, #{reportTarget}, #{reason}
)

mapper.update:
UPDATE report SET handled_by = #{userId}, status = #{status}
WHERE id IN

    #{reportId}
