mapper.select:
SELECT id, email, name, role, fans_count,
       intro, created_at, last_login_at, status,
       avatar_link, background_link from user where id = #{userId}

SELECT id, email, name, role, fans_count,
       intro, created_at, last_login_at, status,
       avatar_link, background_link from user where email = #{email}

mapper.update:
UPDATE user
name = #{name}, intro = #{intro}, avatar_link = #{avatarLink}, background_link = #{backgroundLink},
WHERE id = #{id}
